import react, { useState } from "react";
import Preview from "./Components/Preview";
import "./App.css";

function App() {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [selectedNote, setSelectedNode] = useState(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState(false);

  const getAddNote = () => {
    const onTitleChangeHandler = (event) => {
      setTitle(event.target.value);
      console.log(title);
    };
    const onContentChangeHandler = (event) => {
      setContent(event.target.value);
      console.log(content);
    };
    const onSaveNoteHandler = () => {
      const note = {
        id: new Date(),
        title: title,
        content: content,
      };
      const updatedNotes = [...notes, note];
      setNotes(updatedNotes);
      setCreating(false);
      setSelectedNode(note.id);
      setTitle("");
      setContent("");
    };
    return (
      <div>
        <h2>Add New Note</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="Title"
            value={title}
            onChange={onTitleChangeHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="text"
            value={content}
            onChange={onContentChangeHandler}
          />

          <a href="#" className="button green" onClick={onSaveNoteHandler}>
            Save
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if (notes.length === 0) {
      return <h2>there is no notes</h2>;
    }
    if (!selectedNote) {
      return <h2>Choose one note</h2>;
    }

    const note = notes.find((note) => {
      return note.id === selectedNote;
    });
    return (
      <div>
        <div className="note-operations">
          <a href="#">
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        <div>
          <h2>{note.title}</h2>
          <p>{note.content}</p>
        </div>
      </div>
    );
  };
  const addNoteHandler = () => {
    setCreating(true);
  };
  return (
    <div className="App">
      <div className="notes-section">
        <ul className="notes-list">
          {notes.map((note) => {
            <li className="note-item">{note.title}</li>;
          })}
          <li className="note-item">Note 1</li>
          <li className="note-item">Note 2</li>
          <li className="note-item">Note 3</li>
          <li className="note-item">Note 4</li>
        </ul>
        <button className="add-btn" onClick={addNoteHandler}>
          +
        </button>
      </div>
      <div className="preview-section">
        {creating ? getAddNote() : getPreview()}
      </div>
    </div>
  );
}

export default App;
